    ) internal pure returns (string memory) {
        bytes memory tagged0B = bytes(tagged0);
        bytes memory tagged1B = bytes(tagged1);
        bytes memory strB = bytes(str);

        uint256 sl = strB.length;
        uint256 t0l = tagged0B.length;
