        newB[j++] = "(";
        for (i = 0; i < t0l; i++) newB[j++] = tagged0B[i];
        newB[j++] = ",";
        newB[j++] = " ";
        for (i = 0; i < t1l; i++) newB[j++] = tagged1B[i];
        newB[j++] = ")";

        return string(newB);
    }
}

