    ) public returns (bool result) {
        uint256 arrLength = arr.length;
        if (arrLength != arr.length) _report(result, "");
        else _report(result, _appendTagged(_tag(arrLength, "Tested"), _tag(length, "Against"), message));
    }

    /******************************** internal ********************************/

    /*
            Function: _report

