        fixture.insert(ids[2], keys[2], ids[0], address(0));
        Assert.equal(fixture.getSize(), 6, "wrong size");
        Assert.equal(fixture.getKey(ids[2]), keys[2], "wrong key");
        Assert.equal(fixture.getNext(ids[2]), ids[3], "wrong next");
        Assert.equal(fixture.getPrev(ids[2]), ids[1], "wrong prev");
    }

    function test_insert_findWithHint() public {
        fixture.insert(ids[0], keys[0], address(0), address(0));
        fixture.insert(ids[1], keys[1], ids[0], address(0));
        fixture.insert(ids[2], keys[2], ids[1], address(0));
        fixture.insert(ids[4], keys[4], ids[2], address(0));
        fixture.insert(ids[5], keys[5], ids[4], address(0));

        fixture.insert(ids[3], keys[3], ids[2], ids[4]);
        Assert.equal(fixture.getSize(), 6, "wrong size");
        Assert.equal(fixture.getKey(ids[3]), keys[3], "wrong key");
        Assert.equal(fixture.getNext(ids[3]), ids[4], "wrong next");
        Assert.equal(fixture.getPrev(ids[3]), ids[2], "wrong prev");
    }
}

contracts/test/TestPreciseMathUtils.sol.content:
pragma solidity ^0.5.11;

import "../libraries/PreciseMathUtils.sol";
import "./helpers/truffle/Assert.sol";

contract TestPreciseMathUtils {
    function test_validPerc() public {
        Assert.equal(PreciseMathUtils.validPerc(50), true, "50 should be a valid percentage");
        Assert.equal(PreciseMathUtils.validPerc(0), true, "0 should be a valid percentage");
        Assert.equal(PreciseMathUtils.validPerc(10**27), true, "the max should be a valid percentage");
        Assert.equal(
            PreciseMathUtils.validPerc(10**27 + 1),
            false,
            "1 more than the max should not be valid percentage"
        );
    }

    function test_percOf1() public {
        Assert.equal(PreciseMathUtils.percOf(100, 3, 4), 75, "3/4 of 100 should be 75");
        Assert.equal(PreciseMathUtils.percOf(100, 7, 9), 77, "7/9 of 100 should be 77");
    }

    function test_percOf2() public {
        Assert.equal(PreciseMathUtils.percOf(100, 3), 0, ".0000000000000000000000003% of 100 is 0");
        Assert.equal(PreciseMathUtils.percOf(10**27, 1), 1, ".0000000000000000000000001% of 1000000000 is 1");
        Assert.equal(PreciseMathUtils.percOf(100, 10**27 / 10), 10, "10% of 100 is 10");
    }

    function test_percPoints() public {
        Assert.equal(
            PreciseMathUtils.percPoints(3, 4),
            750000000000000000000000000,
            "3/4 should convert to valid percentage"
        );
        Assert.equal(
            PreciseMathUtils.percPoints(100, 300),
            333333333333333333333333333,
            "100/300 should convert to valid percentage"
        );
    }
}

contracts/test/TestEarningsPoolLIP36.sol.content:
pragma solidity ^0.5.11;

import "./mocks/EarningsPoolFixture.sol";
import "./helpers/truffle/Assert.sol";
import "../libraries/PreciseMathUtils.sol";
import "openzeppelin-solidity/contracts/math/SafeMath.sol";

contract TestEarningsPoolLIP36 {
    using SafeMath for uint256;

    EarningsPoolFixture fixture;

    function beforeEach() public {
        fixture = new EarningsPoolFixture();
        fixture.setStake(1000);
        fixture.setCommission(500000, 500000);
    }

    function test_updateCumulativeFeeFactor_no_prevEarningsPool() public {
        uint256 fees = 1000;

        // earningsPool.cumulativeFeeFactor == 0
        // prevEarningsPool.cumulativeFeeFactor == 0
        // prevEarningsPool.cumulativeRewardFactor == 0
        fixture.updateCumulativeFeeFactor(fees);
        uint256 expFeeFactor = PreciseMathUtils.percPoints(fees, fixture.getTotalStake());
        Assert.equal(fixture.getCumulativeFeeFactor(), expFeeFactor, "should set cumulativeFeeFactor");

        // earningsPool.cumulativeFeeFactor != 0
        fixture.updateCumulativeFeeFactor(fees);
        expFeeFactor = expFeeFactor.add(PreciseMathUtils.percPoints(fees, fixture.getTotalStake()));
        Assert.equal(fixture.getCumulativeFeeFactor(), expFeeFactor, "should update cumulativeFeeFactor");
    }

    function test_updateCumulativeFeeFactor_prevEarningsPool() public {
        uint256 fees = 200;

        // prevEarningsPool.cumulativeFeeFactor = 2
        // prevEarningsPool.cumulativeRewardFactor = 3
        uint256 prevFeeFactor = 2;
        uint256 prevRewFactor = 3;
        fixture.setPrevPoolEarningsFactors(prevFeeFactor, prevRewFactor);

        // earningsPool.cumulativeFeeFactor == 0
        fixture.updateCumulativeFeeFactor(fees);
        uint256 expFeeFactor = prevFeeFactor.add(PreciseMathUtils.percOf(prevRewFactor, fees, fixture.getTotalStake()));
        Assert.equal(fixture.getCumulativeFeeFactor(), expFeeFactor, "should update cumulativeFeeFactor");

        // earningsPool.cumulativeFeeFactor != 0
        fixture.updateCumulativeFeeFactor(fees);
        expFeeFactor = expFeeFactor.add(PreciseMathUtils.percOf(prevRewFactor, fees, fixture.getTotalStake()));
    }

    function test_updateCumulativeRewardFactor() public {
        uint256 rewards = 1000;

        // prevEarningsPool.cumulativeRewardFactor == 0
        uint256 expRewardFactor = PreciseMathUtils.percPoints(1, 1).add(
            PreciseMathUtils.percOf(PreciseMathUtils.percPoints(1, 1), rewards, fixture.getTotalStake())
        );
        fixture.updateCumulativeRewardFactor(1000);
        Assert.equal(expRewardFactor, fixture.getCumulativeRewardFactor(), "incorrect cumulative reward factor");

        // prevEarningsPool.cumulativeRewardFactor != 0
        fixture.setPrevPoolEarningsFactors(0, expRewardFactor);
        expRewardFactor = expRewardFactor.add(
            PreciseMathUtils.percOf(expRewardFactor, rewards, fixture.getTotalStake())
        );
        fixture.updateCumulativeRewardFactor(1000);
        Assert.equal(expRewardFactor, fixture.getCumulativeRewardFactor(), "incorrect cumulative reward factor");
    }
}

